        (original_title, release_date, duration, rating, country_id, created_at, updated_at)
        VALUES
            ($$Inglourious Basterds$$, '2009-01-01', 9180, 8.3, 1, CURRENT_DATE, CURRENT_DATE)
        RETURNING id
