        (original_title, release_date, duration, rating, country_id, created_at, updated_at)
    VALUES
        ($$The Wolf of Wall Street$$, '2013-01-01', 10800, 8.2, 1, CURRENT_DATE, CURRENT_DATE)
    RETURNING id
)
