        (original_title, release_date, duration, rating, country_id, created_at, updated_at)
    VALUES
        ($$Robot Uprising$$, '2023-03-28', 8520, 8.1, 6, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP)
