        (original_title, release_date, duration, rating, country_id, created_at, updated_at)
    VALUES
        ($$Hockey Dynasty$$, '2022-12-01', 7500, 9.2, 5, CURRENT_TIMESTAMP, CURRENT_TIMESTAMP)
